mapper.update:
update `admin`
set adminPassword=#{password},
    version=version + 1
where id = #{id}
  and version = #{version}
  and isDelete is false

mapper.select:
SELECT id, adminName, adminNickname, role, version FROM `admin` WHERE isDelete IS FALSE
AND (adminName LIKE #{query} OR adminNickname LIKE #{query})
limit #{page.pageStart}, #{page.pageSize}

mapper.update:
UPDATE `admin`
SET `isDelete`= TRUE,
    version=version + 1
WHERE `id` = #{id}
  and version = #{version}
  AND `isDelete` IS FALSE
