mapper.select:
SELECT id, siteName, location, seat, hasKeys, isLent, version FROM `site` WHERE isDelete IS FALSE
AND (siteName LIKE #{query} OR location LIKE #{query})
limit #{page.pageStart}, #{page.pageSize}

mapper.update:
UPDATE `site`
SET `isDelete`= TRUE,
    version=version + 1
WHERE `id` = #{id}
  and version = #{version}
  AND `isDelete` IS FALSE
