mapper.update:
UPDATE `user`
SET `userPassword`=#{password},
    version=version + 1
WHERE `id` = #{id}
  AND `isDelete` IS FALSE
  and version = #{version}

mapper.select:
SELECT user.id, user.userName, user.userNickname, user.version, IFNULL(historyNum.sum, 0) `useSum` FROM `user` LEFT JOIN ( SELECT userId, COUNT(*) `sum` FROM history WHERE isDelete IS FALSE GROUP BY userId ) historyNum ON historyNum.userId = user.id WHERE user.isDelete IS FALSE
AND (user.userName LIKE #{query} OR user.userNickname LIKE #{query})
limit #{page.pageStart}, #{page.pageSize}

mapper.update:
UPDATE `user`
SET `userNickname`=#{userNickname},
    version=version + 1
WHERE `id` = #{id}
  and version = #{version}
  AND `isDelete` IS FALSE

UPDATE `user`
SET `isDelete`= TRUE,
    version=version + 1
WHERE `id` = #{id}
  and version = #{version}
  AND `isDelete` IS FALSE
